import React, { useEffect, useRef, useState } from "react";
import { getClientAndConnect } from "../../websocket/Websocket";
import Rooms from "../common/Rooms";
import WebsocketStatus from "../common/WebsocketStatus";
import Board from "./Board";

const Snake = () => {
    const wsClient = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const [error, setError] = useState(null);
    const [room, setRoom] = useState(0);

    useEffect(() => {
        wsClient.current = (getClientAndConnect(setIsConnected, setError));
        return () => wsClient.current.disconnect();
    }, []);

    const body = <div>Error...</div>

    if (isConnected && room === 0) { // user didn't choose a room
        body = <>
            <Rooms />
            <button onClick={() => wsClient.current.send("/app/hello", {}, "hello!")}>
                Send message
            </button>
            <WebsocketStatus isConnected={isConnected} />
        </>
    } else if (isConnected && room > 0) { 
        body = <Board />
    }
    else {
        return <div>Connecting...</div>
    }



}

export default Snake;
