import React, { useEffect, useRef, useState } from "react";
import Scores from "./Scores";
import { getClientAndConnect } from "../../websocket/Websocket";
import Rooms from "../common/Rooms";
import WebsocketStatus from "../common/WebsocketStatus";

const Board = ({ wsRef, roomNum }) => {
    const canvasRef = useRef(null);
    const subRef = useRef(null);
    const [gameState, setGameState] = useState(null);
    const [canvas, setCanvas] = useState({ width: 480, height: 480, color: "oldlace", scale: 20 })


    const onMessage = (payload) => {
        var payload = JSON.parse(payload.body);
    }

    useEffect(() => {
        subRef.current = wsRef.current.subscribe(`topic/snake_room/${roomNum}`, onMessage)
        return () => subRef.unsubscribe();
    }, []);

    return (
        <div className='snake-game'>
            <div role="button" tabIndex="0" onKeyDown={handleKeyDown} {...swipeHandlers} className='snake-container'>
            <h2 style={{ margin: '10px 0px -15px' }} className='text-shadow'>Your color is <span style={{ color: gameState.snakes[socketRef.current.id].color }}>{gameState.snakes[socketRef.current.id].color}</span></h2>
                <canvas style={roomName ? { display: 'block' } : { display: 'none' }}
                    className='snake-canvas'
                    ref={canvasRef}
                    width={`${canvas.width}px`}
                    height={`${canvas.height}px`}
                />
                <div className='options'>
                    <button
                        onClick={() => socketRef.current.emit('newGame', roomName, mode)}
                        className='button-red'>Start game
                    </button>
                    <button
                        onClick={() => socketRef.current.emit('leaveRoom', roomName)}
                        className='button-red'>Back to menu
                    </button>
                </div>{options}
                {gameResult}
                <Scores gameState={gameState} />
            </div>
        </div>
    )

}

export default Board;
